["Cast Start"] = "function (self, unitId, unitFrame, envTable)\n    \n    envTable.UpdateBorder (unitFrame)\n    \nend\n\n\n",
["Destructor"] = "function (self, unitId, unitFrame, envTable)\n    if (unitFrame.castBar.CastBarBorder) then\n        unitFrame.castBar.CastBarBorder:Hide()\n    end    \nend",
["Cast Update"] = "function (self, unitId, unitFrame, envTable)\n    \n    envTable.UpdateBorder (unitFrame)\n    \nend\n\n\n",
["Constructor"] = "function (self, unitId, unitFrame, envTable)\n    \n    --settings\n    \n    --adjust the border offset to correctly fit into the health bar\n    local leftOffset = -60\n    local topOffset = 22\n    local rightOffset = 46\n    local bottomOffset = -22\n    \n    --hide the icon of the spell, may require /reload after changing\n    envTable.HideIcon = false\n    \n    --border settings\n    envTable.BorderThickness = 1\n    envTable.BorderColor = \"black\"\n    \n    local borderTextureImage = [[Interface\\Soulaire Media\\castbar\\castbar.tga]]  \n    local borderTextureImageProtected = [[Interface\\Soulaire Media\\castbar\\castbarUI.tga]]  \n    \n    local castBar = unitFrame.castBar\n    \n    --private\n    --create the border\n    if (not castBar.CastBarBorder) then\n        local squareBorder = CreateFrame (\"frame\", nil, castBar)\n        squareBorder:SetFrameLevel (castBar:GetFrameLevel() + 2)\n        squareBorder:SetPoint (\"topleft\", castBar, \"topleft\", 0, 0)\n        squareBorder:SetPoint (\"bottomright\", castBar, \"bottomright\", 0, 0)\n        castBar.CastBarBorder = squareBorder \n        local innerBorder = Plater:CreateImage (squareBorder, texture) \n        squareBorder.borderTexture = innerBorder\n    end    \n    \n    local borderTexture = castBar.CastBarBorder.borderTexture\n    borderTexture:SetTexture (borderTextureImage)\n    borderTexture:SetVertexColor (DetailsFramework:ParseColors (unitFrame.interuptColor))\n    borderTexture:SetPoint (\"topleft\", castBar, \"topleft\", leftOffset, topOffset)\n    borderTexture:SetPoint (\"bottomright\", castBar, \"bottomright\", rightOffset, bottomOffset)\n    borderTexture:SetAlpha (1) \n    \n    --update the border\n    function envTable.UpdateBorder (unitFrame)\n        \n        local borderTexture = unitFrame.castBar.CastBarBorder.borderTexture\n        borderTexture:SetVertexColor (DetailsFramework:ParseColors (unitFrame.interuptColor))\n        --borderTexture:ClearAllPoints()\n        \n        castBar.Icon:ClearAllPoints()\n        \n        castBar.Icon:SetPoint (\"topright\", castBar, \"topleft\", -1,2)\n        castBar.Icon:SetPoint (\"bottomright\", castBar, \"bottomleft\", 0,-2)\n        castBar.Icon:SetWidth (14)\n        \n        if (castBar.canInterrupt) then\n            borderTexture:SetTexture (borderTextureImage)\n        else\n            borderTexture:SetTexture (borderTextureImageProtected)\n        end\n        \n        if (envTable.HideIcon) then\n            castBar.Icon:Hide()\n        end\n        \n        castBar.CastBarBorder:Show()\n    end\n    \nend\n\n\n\n\n",
},
