["Nameplate Added"] = "function (self, unitId, unitFrame, envTable,modTable)\n    --adjust the border offset to correctly fit into the health bar\n    local leftOffset = -6\n    local topOffset = 3\n    local rightOffset =6\n    local bottomOffset = -3\n    \n    --adjust the border texture \n    \n    \n    local borderTextureImage = [[Interface\\Soulaire Media\\borders\\healthbar.tga]]  \n    local levelBorderTextureImage = [[Interface\\Soulaire Media\\borders\\level.tga]]\n    local levelBGTextureImage = [[Interface\\AddOns\\Plater\\images\\platetexture.blp]]\n    local questIconAtlas =\"AutoQuest-Badge-Campaign\"\n    --adjust the alpha\n    --local alpha = 2\n    \n    \n    local healthBar = unitFrame.healthBar\n    \n    --private\n    \n    \n    if Plater.db.profile.plate_config[unitFrame.actorType].level_text_enabled then\n        \n        if (not unitFrame.healthBar.levelBackdrop) then   \n            local levelBackdrop = CreateFrame (\"frame\", nil, unitFrame.healthBar)\n            levelBackdrop:SetFrameLevel (unitFrame.healthBar:GetFrameLevel() - 1)\n            levelBackdrop:SetPoint (\"topleft\", unitFrame.healthBar, \"topleft\", 0, 0)\n            levelBackdrop:SetPoint (\"bottomright\", unitFrame.healthBar, \"bottomright\", 0, 0)\n            unitFrame.healthBar.levelBackdrop = levelBackdrop \n            local innerBorder = Plater:CreateImage (levelBackdrop, texture) \n            levelBackdrop.borderTexture = innerBorder\n        end \n        \n        if (not unitFrame.healthBar.levelBorder) then   \n            local levelBorder = CreateFrame (\"frame\", nil, unitFrame.healthBar)\n            levelBorder:SetFrameLevel (unitFrame.healthBar:GetFrameLevel() + 0)\n            levelBorder:SetPoint (\"topleft\", unitFrame.healthBar, \"topleft\", 0, 0)\n            levelBorder:SetPoint (\"bottomright\", unitFrame.healthBar, \"bottomright\", 0, 0)\n            unitFrame.healthBar.levelBorder = levelBorder \n            local innerBorder = Plater:CreateImage (levelBorder, texture) \n            levelBorder.borderTexture = innerBorder\n        end\n        \n        local levelTexture = healthBar.levelBorder.borderTexture\n        local levelBGTexture = healthBar.levelBackdrop.borderTexture\n        \n        \n        levelBGTexture:SetPoint (\"topleft\", healthBar, \"topright\", 0, 0)\n        levelBGTexture:SetPoint (\"bottomright\", healthBar, \"bottomright\", 14, 0)\n        levelTexture:SetPoint (\"topleft\",healthBar, \"topright\", -5, 9)\n        levelTexture:SetPoint (\"bottomright\", healthBar, \"bottomright\", 18, -9)\n        \n        \n        levelTexture:SetVertexColor (unpack(modTable.config.borderColor))\n        levelBGTexture:SetVertexColor (DetailsFramework:ParseColors (\"0.8,0.6,0\"))\n        levelTexture:SetAlpha (1) \n        levelBGTexture:SetAlpha (1.0) \n        levelTexture:SetTexture (levelBorderTextureImage)\n        levelBGTexture:SetTexture (levelBGTextureImage)\n        \n    end\n    \n    if (not unitFrame.healthBar.squareBorder) then   \n        local squareBorder = CreateFrame (\"frame\", nil, unitFrame.healthBar)\n        squareBorder:SetFrameLevel (unitFrame.healthBar:GetFrameLevel() + 0)\n        squareBorder:SetPoint (\"topleft\", unitFrame.healthBar, \"topleft\", 0, 0)\n        squareBorder:SetPoint (\"bottomright\", unitFrame.healthBar, \"bottomright\", 0, 0)\n        unitFrame.healthBar.squareBorder = squareBorder \n        local innerBorder = Plater:CreateImage (squareBorder, texture) \n        squareBorder.borderTexture = innerBorder\n    end\n    \n    if (not unitFrame.healthBar.questIcon) then   \n        local questIcon = CreateFrame (\"frame\", nil, unitFrame.healthBar)\n        questIcon:SetFrameLevel (unitFrame.healthBar:GetFrameLevel() + 3)\n        questIcon:SetPoint (\"topleft\", unitFrame.healthBar, \"topleft\", 0, 0)\n        questIcon:SetPoint (\"bottomright\", unitFrame.healthBar, \"bottomright\", 0, 0)\n        \n        local questTexture = Plater:CreateImage (questIcon, texture) \n        \n        questTexture:SetPoint (\"topleft\", healthBar, \"topleft\", -4, -2)\n        questTexture:SetPoint (\"bottomright\", healthBar, \"bottomleft\", 10, -10)\n        questTexture:SetAtlas(questIconAtlas)\n        unitFrame.healthBar.questIcon = questIcon \n        questIcon.texture = questTexture\n    end\n    \n    --if (not unitFrame.healthBar.questText) then   \n    --    local border = CreateFrame (\"frame\", nil, unitFrame.healthBar)\n    --    border:SetFrameLevel (unitFrame.healthBar:GetFrameLevel() + 3)\n    --    border:SetPoint (\"topleft\", unitFrame.healthBar, \"topleft\", 0, 0)\n    --    border:SetPoint (\"bottomright\", unitFrame.healthBar, \"bottomright\", 0, 0)\n    --    unitFrame.healthBar.questText = border \n    --    local text =border:CreateFontString(nil,\"ARTWORK\")\n    --    text:SetFont(\"Fonts\\\\FRIZQT__.TTF\", 6, \"OUTLINE\")\n    --    text:SetJustifyH(\"LEFT\")\n    --    text:SetJustifyV(\"CENTER\")\n    --    \n    --    border.text = text\n    --end\n    \n    local borderTexture = healthBar.squareBorder.borderTexture\n    \n    borderTexture:SetPoint (\"topleft\", healthBar, \"topleft\", leftOffset, topOffset)\n    borderTexture:SetPoint (\"bottomright\", healthBar, \"bottomright\", rightOffset, bottomOffset)\n    \n    borderTexture:SetVertexColor (unpack(modTable.config.borderColor))\n    borderTexture:SetAlpha (1) \n    borderTexture:SetTexture (borderTextureImage)\n    \n    --local questText = unitFrame.healthBar.questText.text\n    if (unitFrame.namePlateIsQuestObjective) then\n        unitFrame.healthBar.questIcon:Show()\n        --questText:SetPoint (\"CENTER\", healthBar, \"topleft\", 16, -8)\n        --questText:SetText(unitFrame.QuestAmountCurrent .. \"/\" .. unitFrame.QuestAmountTotal)\n        --questText:SetAlpha(1)\n    else\n        unitFrame.healthBar.questIcon:Hide()\n        --questText:SetAlpha(0)\n    end\n    \n    --questText:SetText(\"test\")\n    \n    -------------------\n    envTable.Update = function(unitFrame)\n        \n       -- local borderTexture = unitFrame.healthBar.squareBorder.borderTexture\n       -- if self.IsTarget then\n       --     borderTexture:SetVertexColor (unpack(modTable.config.selectedBorderColor))\n        --else\n        --    borderTexture:SetVertexColor (unpack(modTable.config.borderColor))\n        --end\n        \n        if not Plater.db.profile.plate_config[unitFrame.actorType].level_text_enabled then\n            if (unitFrame.healthBar.levelBackdrop) then\n                local levelBGTexture = healthBar.levelBackdrop.borderTexture\n                levelBGTexture:Hide()\n            end\n            \n            if (unitFrame.healthBar.levelBorder) then\n                local levelTexture = healthBar.levelBorder.borderTexture\n                levelTexture:Hide()\n            end\n        else\n            if (unitFrame.healthBar.levelBackdrop) then\n                local levelBGTexture = healthBar.levelBackdrop.borderTexture\n                levelBGTexture:Show()\n            end\n            \n            if (unitFrame.healthBar.levelBorder) then\n                local levelTexture = healthBar.levelBorder.borderTexture\n                levelTexture:Show()\n            end\n        end\n        --if (unitFrame.namePlateIsQuestObjective) then\n        --    local questText = unitFrame.healthBar.questText.text\n        --    questText:SetText(unitFrame.QuestAmountCurrent .. \"/\" .. unitFrame.QuestAmountTotal)\n        --end\n        \n    end\n    \nend",
