["Nameplate Added"] = "function (self, unitId, unitFrame, envTable, modTable)\n    --adjust the border texture \n    local borderColor = \"1.0,0.75,0\" \n    \n    local portraitBorderTextureImage = [[Interface\\Soulaire Media\\borders\\portrait.tga]]  \n    \n    --Classifications\n    local eliteImage = [[Interface\\Soulaire Media\\classifications\\elite.tga]]\n    local rareImage = [[Interface\\Soulaire Media\\classifications\\rare.tga]]\n    local rareEliteImage = [[Interface\\Soulaire Media\\classifications\\rareelite.tga]]\n    local worldbossImage = [[Interface\\Soulaire Media\\classifications\\worldboss.tga]]\n    \n    local glow = \"ChallengeMode-KeystoneSlotFrameGlow\"\n    \n    local healthBar = unitFrame.healthBar\n    \n    --private\n    \n    if (not unitFrame.portrait) then   \n        local portrait = CreateFrame (\"frame\", nil, healthBar)\n        portrait:SetFrameLevel (healthBar:GetFrameLevel() + 2)\n        portrait:SetPoint (\"topleft\", healthBar, \"topleft\", 0, 0)\n        portrait:SetPoint (\"bottomright\", healthBar, \"bottomright\", 0, 0)\n        unitFrame.portrait = portrait \n        \n        local portraitTexture = portrait:CreateTexture(nil, \"BACKGROUND\")\n        portraitTexture:SetHeight(64)\n        portraitTexture:SetWidth(64)\n        portrait.portraitTexture = portraitTexture\n        \n        portraitTexture:SetPoint (\"topleft\", healthBar, \"topleft\", -16, 4)\n        portraitTexture:SetPoint (\"bottomright\", healthBar, \"bottomleft\", 2, -4)\n        \n        portraitTexture:SetVertexColor (DetailsFramework:ParseColors (\"white\"))\n        portraitTexture:Show()\n        \n        local ccTexture = portrait:CreateTexture(nil, \"BACKGROUND\")\n        ccTexture:SetHeight(64)\n        ccTexture:SetWidth(64)\n        ccTexture:Show()\n        portrait.ccTexture = ccTexture\n        \n        ccTexture:SetPoint (\"topleft\", healthBar, \"topleft\", -16, 4)\n        ccTexture:SetPoint (\"bottomright\", healthBar, \"bottomleft\", 2, -4)\n        \n        local ccCooldown= CreateFrame(\"Cooldown\", nil,  portrait)\n        ccCooldown:SetSwipeTexture(\"Interface\\\\PVPFrame\\\\PVP-Separation-Circle-Cooldown-overlay\")\n        ccCooldown:SetSwipeColor(0, 0, 0, 0.8)\n        ccCooldown:SetSize(50, 50)\n        \n        ccCooldown:SetPoint (\"topleft\", healthBar, \"topleft\", -20, 9)\n        ccCooldown:SetPoint (\"bottomright\", healthBar, \"bottomleft\", 6, -8)\n        local ccTimer = ccCooldown:GetRegions()\n        ccTimer:SetFont(\"Fonts\\\\FRIZQT__.TTF\", modTable.config.ccFontSize, \"OUTLINE\")\n        portrait.ccCooldown = ccCooldown\n        \n        local ccMask = portrait:CreateMaskTexture(nil, \"OVERLAY\", nil, 1)\n        ccMask:Show()\n        ccMask:SetAtlas(\"CircleMaskScalable\", true)\n        ccTexture:AddMaskTexture(ccMask)\n        portrait.ccMask = ccMask;\n        \n        ccMask:SetPoint (\"topleft\", healthBar, \"topleft\", -16, 4)\n        ccMask:SetPoint (\"bottomright\", healthBar, \"bottomleft\", 2, -4)\n        \n        local portraitBorderTexture = portrait:CreateTexture(nil, \"OVERLAY\")\n        portrait.portraitBorderTexture = portraitBorderTexture\n        \n        portraitBorderTexture:SetPoint (\"topleft\", healthBar, \"topleft\", -22, 11)\n        portraitBorderTexture:SetPoint (\"bottomright\", healthBar, \"bottomleft\", 8, -11)\n        \n        portraitBorderTexture:SetVertexColor (DetailsFramework:ParseColors (borderColor))\n        portraitBorderTexture:SetTexture (portraitBorderTextureImage)\n        \n        local classificationTexture = portrait:CreateTexture(nil, \"OVERLAY\",nil,1)\n        portrait.classificationTexture = classificationTexture\n        \n        classificationTexture:SetPoint (\"topleft\", healthBar, \"topleft\", -28, 15)\n        classificationTexture:SetPoint (\"bottomright\", healthBar, \"bottomleft\", 8, -17)\n        \n        classificationTexture:SetVertexColor (DetailsFramework:ParseColors (\"white\"))\n        \n        local glowTexture = portrait:CreateTexture(nil, \"OVERLAY\",nil,2)\n        portrait.glowTexture = glowTexture\n        glowTexture:SetPoint (\"topleft\", healthBar, \"topleft\", -25.5, 15)\n        glowTexture:SetPoint (\"bottomright\", healthBar, \"bottomleft\", 11.5, -14.5)\n        glowTexture:SetAtlas(glow)\n    end\n    \n    --Update internal portrait texture to the current unit\n    SetPortraitTexture(unitFrame.portrait.portraitTexture, unitFrame.unit)\n    \n    --Workout if stunnable\n    \n    local unit = unitFrame.namePlateUnitToken\n    local guid = UnitGUID(unit)\n    local npcID = select(6, strsplit(\"-\", guid))\n    local is_stunnable = true\n    \n    if modTable.dungeon_npc_list then\n        local npc_data = modTable.dungeon_npc_list[npcID]\n        \n        if npc_data and npc_data.characteristics then\n            is_stunnable = npc_data.characteristics.Stun\n        end\n    end\n    \n    \n    local unitClassification = UnitClassification(unit)\n    local level = UnitLevel(unit)\n    local classificationTexture = unitFrame.portrait.classificationTexture\n    \n    if (classificationTexture) then\n        classificationTexture:Show()\n        if (unitFrame.namePlateClassification == \"worldboss\") then\n            -- or unitClassification == \"rareelite\"\n            classificationTexture:SetTexture (worldbossImage)\n        elseif unitFrame.namePlateClassification == \"elite\"and not IsInInstance() or level==-1 or not is_stunnable then   \n            classificationTexture:SetTexture (eliteImage)\n        elseif unitFrame.namePlateClassification == \"rare\" then   \n            classificationTexture:SetTexture (rareImage)\n        elseif unitFrame.namePlateClassification == \"rareelite\" then   \n            classificationTexture:SetTexture (rareEliteImage)\n        else\n            classificationTexture:Hide()    \n        end\n    end\nend",
["Mod Option Changed"] = "function (self, unitId, unitFrame, envTable, modTable)\n    if (unitFrame and unitFrame.portrait) then\n        local ccTimer = unitFrame.ccCooldown:GetRegions()\n        ccTimer:SetFont(\"Fonts\\\\FRIZQT__.TTF\", modTable.config.ccFontSize, \"OUTLINE\")\n    end\n    \nend\n\n\n\n\n",
["Nameplate Updated"] = "function (self, unitId, unitFrame, envTable, modTable)\n    modTable:UpdateCrowdControlAuras(unitFrame)\nend\n\n\n",
["Initialization"] = "function (modTable)\n    --Only do MDT lookups if markNonStunnable is enabled\n    if modTable.config.markNonStunnable then\n        if (not modTable.dungeon_npc_list) then\n            modTable.dungeon_npc_list = {}\n        end\n        \n        local DungeonTools = MDT\n        \n        if (DungeonTools) then\n            \n            function modTable.getNpcInfo()\n                local info = {}\n                \n                local zone_id = C_Map.GetBestMapForUnit(\"player\")\n                local dungeon_idx = DungeonTools.zoneIdToDungeonIdx[zone_id]\n                local dungeon_data = DungeonTools.dungeonEnemies[dungeon_idx]\n                \n                if dungeon_data then\n                    for _,w in pairs(dungeon_data) do\n                        local id = \"\" .. w.id\n                        info[id] = w\n                    end\n                end\n                \n                modTable.dungeon_npc_list = info\n            end\n        end\n        \n        \n        function modTable.UpdateCrowdControlAuras(self,unitFrame)\n            local portrait = unitFrame.portrait\n            if portrait then\n                local spellID, expirationTime, duration\n                for _, auraIcon in ipairs (unitFrame.ExtraIconFrame.IconPool) do\n                    if auraIcon:IsShown() then\n                        if DetailsFramework.CrowdControlSpells[auraIcon.spellId] then\n                            local thisExpi = auraIcon.startTime + auraIcon.duration\n                            if not expirationTime or expirationTime < thisExpi then\n                                spellID = auraIcon.spellId\n                                expirationTime = thisExpi\n                                duration = auraIcon.duration\n                            end\n                            if modTable.config.hideIcons then\n                                auraIcon:SetSize(1,1)\n                                auraIcon:SetAlpha(0)\n                                --auraIcon:Hide()\n                            end\n                        else\n                            auraIcon:SetAlpha(1)\n                        end\n                    else\n                        auraIcon:SetAlpha(1)\n                        --break\n                    end\n                end\n                \n                local hasCC = spellID and expirationTime\n                if hasCC then\n                    portrait.ccCooldown:SetCooldown(expirationTime - duration, duration)\n                    if spellID ~= nil then\n                        local icon = select(1, C_Spell.GetSpellInfo(spellID))\n                        if icon then\n                            portrait.ccTexture:SetTexture(icon[\"iconID\"])\n                            end\n                        end\n                    end\n                    portrait.portraitTexture:SetShown(not hasCC)\n                    portrait.ccTexture:SetShown(hasCC)\n                    portrait.glowTexture:SetShown(hasCC)\n                    \n                    portrait.ccExpirationTime = expirationTime\n                    portrait.ccSpellID = spellID\n                    \n                    local timeRemaining = portrait.ccExpirationTime and math.max(portrait.ccExpirationTime - GetTime(), 0) or 0\n                    if timeRemaining <= 0 then\n                        portrait.ccCooldown:Clear()\n                    end\n                end\n            end\n        end\n    end\n    \n    \n    \n\n",
["Zone Changed"] = "function (self, unitId, unitFrame, envTable, modTable)\n    if not IsInInstance() then return end\n    \n    modTable.keystoneLevel = C_ChallengeMode.GetActiveKeystoneInfo() or 0;\n    \n    local instanceId, _ = select(8,GetInstanceInfo())\n    modTable.currentDungeon = instanceId\n    \n    if (not modTable.keystoneLevel or not modTable.currentDungeon) then\n        return\n    end\n    \n    if modTable.getNpcInfo then\n        modTable:getNpcInfo()\n    end\n    \nend",
},
