["source"] = "import",
["customGrow"] = "function(newPositions, activeRegions)\n    --First Row settings\n    local FirstRowLimit = 7 -- limit of icons in first row\n    local WidthFirstRow = 40 -- width of first row icons\n    local HeightFirstRow = 40 -- height of first row icons\n    local StrataFirstRow = \"LOW\" -- frame strata of first row icons\n    \n    --Second Row settings\n    local SecondRowLimit = 10 -- limit of icons in second row\n    local WidthSecondRow = 30 -- width of second row icons\n    local HeightSecondRow = 30 -- height of second row icons\n    local StrataSecondRow = \"BACKGROUND\" -- frame strata of second row icons\n    \n    -- Other Settings: Icon Spacing & Vertical Space between 1st Row & 2nd Row\n    local spacing = 2 -- spacing between icons\n    local correctionYspacing = -1 -- Vertical space between 1st & 2nd row (- for higher, + for lower 2nd row)\n    \n    --END OF QUAZII CUSTOM GROW SETTINGS - DO NOT MODIFY BELOW --\n    \n    local xCount = 0\n    \n    local xOffset = 0\n    local yOffset = 0\n    local total = #activeRegions\n    \n    for i, regionData in ipairs(activeRegions) do\n        local region = regionData.region\n        \n        \n        local rowTotal = 1\n        \n        local localWidth = 1\n        local localHeight = 1\n        local currentRow = 0\n        \n        if i <= FirstRowLimit then\n            localWidth = WidthFirstRow\n            localHeight = HeightFirstRow\n            localFrameStrata = StrataFirstRow\n            \n            if total <= FirstRowLimit then\n                rowTotal = total\n            else\n                rowTotal = FirstRowLimit\n            end\n            \n        elseif i <= SecondRowLimit + FirstRowLimit then\n            localWidth = WidthSecondRow\n            localHeight = HeightSecondRow\n            localFrameStrata = StrataSecondRow\n            currentRow = 1\n            \n            if total <= FirstRowLimit + SecondRowLimit then\n                rowTotal = total - FirstRowLimit\n            else\n                rowTotal = SecondRowLimit\n            end\n        end\n        \n        region:SetRegionWidth(localWidth)\n        region:SetRegionHeight(localHeight)\n        region:SetFrameStrata(localFrameStrata)\n        \n        \n        xOffset = 0 - (region.width + spacing) / 2 * (rowTotal-1) + (xCount * (region.width + spacing))\n        \n        if currentRow == 1 then\n            yOffset = 0 - HeightFirstRow - spacing - correctionYspacing\n        elseif currentRow == 2 then\n            yOffset =  0 - (HeightFirstRow + HeightSecondRow) - spacing * 2 - correctionYspacing\n        end\n        \n        xCount = xCount + 1\n        \n        if xCount >= rowTotal then\n            xCount = 0\n        end\n        \n        newPositions[i] = {xOffset, yOffset}\n    end\nend\n\n\n\n\n",
