["customGrow"] = "function(newPositions, activeRegions)\n    --First Row settings\n    local FirstRowLimit = 7 -- limit of icons in first row\n    local WidthFirstRow = 40 -- width of first row icons\n    local HeightFirstRow = 40 -- height of first row icons\n    local StrataFirstRow = \"LOW\" -- frame strata of first row icons\n    \n    --Second Row settings\n    local SecondRowLimit = 10 -- limit of icons in second row\n    local WidthSecondRow = 35 -- width of second row icons\n    local HeightSecondRow = 35 -- height of second row icons\n    local StrataSecondRow = \"BACKGROUND\" -- frame strata of second row icons\n    \n    --Third Row settings\n    local ThirdRowLimit = 0 -- limit of icons in third row\n    local WidthThirdRow = 34.6 -- width of third row icons\n    local HeightThirdRow = 25.95 -- height of third row icons\n    local StrataThirdRow = \"BACKGROUND\" -- frame strata of third row icons\n    \n    -- Other Settings: Icon Spacing & Vertical Space\n    local spacing = 2 -- spacing between icons\n    local correctionYspacing1 = -1.7 -- Vertical space between 1st & 2nd row (- for higher, + for lower 2nd row)\n    local correctionYspacing2 = -1.7 -- Vertical space between 2nd & 3rd row (- for higher, + for lower 3rd row)\n    \n    --END OF QUAZII CUSTOM GROW SETTINGS - DO NOT MODIFY BELOW --\n    \n    local function processRow(startIndex, endIndex, width, height, strata, yOffset)\n        local xCount = 0\n        local rowTotal = endIndex - startIndex + 1\n        for i = startIndex, endIndex do\n            if activeRegions[i] then\n                local region = activeRegions[i].region\n                region:Show()\n                region:SetRegionWidth(width)\n                region:SetRegionHeight(height)\n                region:SetFrameStrata(strata)\n                \n                local xOffset = 0 - (width + spacing) / 2 * (rowTotal-1) + (xCount * (width + spacing))\n                newPositions[i] = {xOffset, yOffset}\n                \n                xCount = xCount + 1\n            end\n        end\n    end\n    \n    local totalIcons = #activeRegions\n    local firstRowEnd = math.min(FirstRowLimit, totalIcons)\n    local secondRowEnd = math.min(FirstRowLimit + SecondRowLimit, totalIcons)\n    local thirdRowEnd = math.min(FirstRowLimit + SecondRowLimit + ThirdRowLimit, totalIcons)\n    \n    -- Process first row\n    processRow(1, firstRowEnd, WidthFirstRow, HeightFirstRow, StrataFirstRow, 0)\n    \n    -- Process second row\n    if firstRowEnd < totalIcons then\n        processRow(firstRowEnd + 1, secondRowEnd, WidthSecondRow, HeightSecondRow, StrataSecondRow, \n        0 - HeightFirstRow - spacing - correctionYspacing1)\n    end\n    \n    -- Process third row\n    if secondRowEnd < totalIcons and ThirdRowLimit > 0 then\n        processRow(secondRowEnd + 1, thirdRowEnd, WidthThirdRow, HeightThirdRow, StrataThirdRow, \n            0 - (HeightFirstRow + HeightSecondRow) - spacing * 2 - correctionYspacing1 - correctionYspacing2)\n    end\n    \n    -- Hide any remaining icons\n    for i = thirdRowEnd + 1, totalIcons do\n        if activeRegions[i] then\n            activeRegions[i].region:Hide()\n            newPositions[i] = {0, 0}\n        end\n    end\nend",
