["custom"] = "function()\n    local helmetSlot = 1\n    local cloakSlot = 15\n    local shouldersSlot = 3   -- Assuming slot 3 for shoulders\n    local chestSlot = 5       -- Assuming slot 5 for chest\n    local isHelmetHeirloom = false\n    local isCloakHeirloom = false\n    local isShouldersHeirloom = false\n    local isChestHeirloom = false\n    \n    -- Check if helmet is heirloom\n    local helmet = GetInventoryItemLink(\"player\", helmetSlot)\n    if helmet then\n        local _, _, quality = GetItemInfo(helmet)\n        if quality == 7 then\n            isHelmetHeirloom = true\n        end\n    end\n    \n    -- Check if cloak is heirloom\n    local cloak = GetInventoryItemLink(\"player\", cloakSlot)\n    if cloak then\n        local _, _, quality = GetItemInfo(cloak)\n        if quality == 7 then\n            isCloakHeirloom = true\n        end\n    end\n    \n    -- Check if shoulders are heirloom\n    local shoulders = GetInventoryItemLink(\"player\", shouldersSlot)\n    if shoulders then\n        local _, _, quality = GetItemInfo(shoulders)\n        if quality == 7 then\n            isShouldersHeirloom = true\n        end\n    end\n    \n    -- Check if chest is heirloom\n    local chest = GetInventoryItemLink(\"player\", chestSlot)\n    if chest then\n        local _, _, quality = GetItemInfo(chest)\n        if quality == 7 then\n            isChestHeirloom = true\n        end\n    end\n    \n    -- Determine the base bonus QXP based on equipped heirlooms\n    local baseQXP = 1\n    if isHelmetHeirloom then\n        baseQXP = baseQXP + 0.10\n    end\n    if isCloakHeirloom then\n        baseQXP = baseQXP + 0.05\n    end\n    if UnitLevel(\"player\") < 80 then\n        if isShouldersHeirloom then\n            baseQXP = baseQXP + 0.10\n        end\n        if isChestHeirloom then\n            baseQXP = baseQXP + 0.10\n        end\n    end\n    \n    -- Check if spell ID 78632 is learned and add 0.10 to the multiplier if true\n    if IsSpellKnown(78632) then\n        baseQXP = baseQXP + 0.10\n    end\n    \n    -- Check for buff with ID 86963 (add 0.10 to the multiplier if the player has the buff)\n    local i = 1\n    while true do\n        local name, _, _, _, _, _, _, _, _, spellId = UnitBuff(\"player\", i)\n        if not name then break end\n        if spellId == 86963 then\n            baseQXP = baseQXP + 0.10\n            break\n        end\n        i = i + 1\n    end\n    \n    aura_env.heirloomBonusQXP = baseQXP\n    aura_env.UpdateQuestXP()\nend",
